import React from 'react';
import { useParams, useHistory, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import withDataFetching from '../withDataFetching';
import SubHeader from '../components/Header/SubHeader';
import ListItem from '../components/ListItem/ListItem';

const ListItemWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  margin: 2% 5%;
`;

const Alert = styled.span`
  width: 100%;
  text-align: center;
`;

const List = ({ data, loading, error, lists }) => {
  let { id } = useParams();
  let history = useHistory();
  let location = useLocation();
  console.log(history);
  let list;
  if (lists.length > 0) {
    list = lists && lists.find(list => list.id === parseInt(id));
  }

  let items;
  if (data.length > 0) {
    items = data.filter(item => item.listId === parseInt(id));
  }

  return !loading && !error ? (
    <>
      {history && list (
        <SubHeader
          goBack={() => history.goBack()}
          title={list.title}
          openForm={() => history.push(`${location.pathname}/new`)}
        />
      )}
      <ListItemWrapper>
        {items && items.map(item => <ListItem key={item.id} data={item} />)}
      </ListItemWrapper>
    </>
  ) : (
    <Alert>{loading ? 'Loading...' : error}</Alert>
  );
};

export default withDataFetching(List);
