import React from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { Route, Switch } from 'react-router-dom';
import Header from '../components/Header/Header';
import ListContextProvider, { ListContext } from '../Context/ListContextProvider';
import Lists from './Lists';
import List from './List';
import Form from './Form';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen",
      "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue",
      sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
`;

const AppWrapper = styled.div`
  text-align: center;
`;

const App = () => (
  <>
    <GlobalStyle />
    <AppWrapper>
      <Header />
      <ListContextProvider>
        <Switch>
          <Route exact path='/'>
            <Lists dataSource={'http://my-json-server.typicode.com/PacktPublishing/React-Projects/lists'}/>
          </Route>
          <Route path='/list/:id/new' component={Form} />
          <Route path='/list/:id'>
            <List dataSource={'http://my-json-server.typicode.com/pranayfpackt/-React-Projects/items'}/>
          </Route>
        </Switch>
      </ListContextProvider>
    </AppWrapper>
  </>
);

export default App;
